data class KakaoSearchBlogResult(
    val meta: Meta,
    val documents: List<Document>,
) {
    @JsonNaming(PropertyNamingStrategies.SnakeCaseStrategy::class)
    data class Meta(
        val totalCount: Int,
        val pageableCount: Int,
        val isEnd: Boolean,
    )

    data class Document(
        val title: String,
        val contents: String,
        val url: String,
        val blogname: String,
        val thumbnail: String,
        val datetime: String,
    ) {
        fun toDomain() =
            Blog(
                title = title,
                contents = contents,
                url = url,
                blogName = blogname,
                datetime = datetime,
                blogVendorType = BlogVendorType.Kakao,
            )
    }

    fun getBlogs() = documents.map { it.toDomain() }
}
